mapper.update:
UPDATE myy1_yytjgysxx ysxx INNER JOIN view_yy_gys_gx xx SET ysxx.t01_gys_yyzz = xx.t01_gys_yyzz, ysxx.t02_gys_jyxkz = xx.t02_gys_jyxkz, ysxx.t03_gys_swdjz = xx.t03_gys_swdjz, ysxx.t04_gys_jxsqs = xx.t04_gys_jxsqs, ysxx.t05_gys_xsrywts = xx.t05_gys_xsrywts, ysxx.t06_gys_shfwcns = xx.t06_gys_shfwcns, ysxx.t07_cj_yyzz = xx.t07_cj_yyzz, ysxx.t08_cj_scxkz = xx.t08_cj_scxkz, ysxx.t09_cj_spzcz = xx.t09_cj_spzcz,
ysxx.g01_gys_yyzz = xx.g01_gys_yyzz, ysxx.g02_gys_jyxkz = xx.g02_gys_jyxkz, ysxx.g03_gys_swdjz = xx.g03_gys_swdjz, ysxx.g04_gys_jxsqs = xx.g04_gys_jxsqs, ysxx.g05_gys_xsrywts = xx.g05_gys_xsrywts, ysxx.g06_gys_shfwcns = xx.g06_gys_shfwcns, ysxx.g07_cj_yyzz = xx.g07_cj_yyzz, ysxx.g08_cj_scxkz = xx.g08_cj_scxkz, ysxx.g09_cj_spzcz = xx.g09_cj_spzcz, ysxx.ddd = xx.ddd,ysxx.uu1 = NOW()
,ysxx.uu2 = #{uu2}
WHERE ysxx.puk = xx.puk AND ysxx.p01_yyid = xx.p01_yyid
AND xx.puk = #{puk}
AND xx.p01_yyid = #{p01_yyid}

mapper.select:

    	SELECT * , datediff(f05_yxzzrq,#{cc1}) as ddd 
    	FROM
			view_gys_zj



				AND k01_gysid = #{k01_gysid}


			 AND datediff(f05_yxzzrq,#{cc1}) < ${ddd} 


             AND datediff(f05_yxzzrq,#{eb5}) < ${ddd} AND datediff(f05_yxzzrq,#{eb5}) > 0 


	        	AND INSTR(fb1,#{fb1})>0


	        	AND k03_zjlb = #{k03_zjlb}


	        	AND INSTR(k02_zjbh,#{k02_zjbh})>0
